mapper.insert:
INSERT /*FileManageDAO.insertFileMaster*/ INTO CO_FILE_MST
	(ATCH_FILE_ID
	, USE_YN
	, REG_ID
	, REG_IP 
	, REG_DTM
	, MOD_ID
	, MOD_IP 
	, MOD_DTM
	)
VALUES
	(#{atchFileId}
	, 'Y'
	, #{regId, jdbcType=VARCHAR}
	, #{regIp, jdbcType=VARCHAR}
	, SYSDATE
	, #{modId, jdbcType=VARCHAR}
	, #{modIp, jdbcType=VARCHAR}
	, SYSDATE
	)

INSERT /*FileManageDAO.insertFileDetail*/ INTO CO_FILE_DTL
	(ATCH_FILE_ID
	, FILE_SEQ
	, PHY_PATH
	, SAVE_FILE_NM
	, REAL_FILE_NM
	, FILE_EXTN
	, FILE_DSC
	, FILE_SIZE
	, USE_YN
	, REG_ID
	, REG_IP 
	, REG_DTM
	, MOD_ID
	, MOD_IP 
	, MOD_DTM
	)
VALUES
	(#{atchFileId}
	, #{fileSeq}
	, #{phyPath}
	, #{saveFileNm}
	, #{realFileNm}
	, #{fileExtn}
	, #{fileDsc, jdbcType=VARCHAR}
	, #{fileSize}
	, 'Y'
	, #{regId, jdbcType=VARCHAR}
	, #{regIp, jdbcType=VARCHAR}
	, SYSDATE
	, #{modId, jdbcType=VARCHAR}
	, #{regIp, jdbcType=VARCHAR}
	, SYSDATE
	)

mapper.update:
UPDATE /* FileManageDAO.updateFileDetail */ CO_FILE_DTL
   SET FILE_DSC = #{fileDsc}
       , MOD_ID = #{modId, jdbcType=VARCHAR}
	   , MOD_IP = #{modIp, jdbcType=VARCHAR}
	   , MOD_DTM = SYSDATE
 WHERE ATCH_FILE_ID = #{atchFileId}
   AND FILE_SEQ = #{fileSeq}

mapper.delete:
DELETE /* FileManageDAO.deleteFileDetail */ 
  FROM CO_FILE_DTL
 WHERE ATCH_FILE_ID = #{atchFileId}
   AND FILE_SEQ = #{fileSeq}

mapper.update:
UPDATE /* FileManageDAO.deleteAllFileInf */ CO_FILE_MST
  SET USE_YN = 'N'
WHERE ATCH_FILE_ID = #{atchFileId}

		UPDATE /* FileMnageDAO.deleteFileInf */ CO_FILE_DTL 
		   SET USE_YN = 'N'
		   	   , MOD_ID = #{modId, jdbcType=VARCHAR}
			   , MOD_IP = #{modIp, jdbcType=VARCHAR}
			   , MOD_DTM = SYSDATE
 		 WHERE ATCH_FILE_ID = #{atchFileId}
 		   AND FILE_SEQ = #{fileSeq}

mapper.select:
SELECT /* FileManageDAO.selectFileListByFileNm */ TBL.* FROM ( SELECT ROWNUM RNUM, T1.* FROM ( SELECT A.ATCH_FILE_ID , B.FILE_SEQ , B.PHY_PATH , B.SAVE_FILE_NM , B.REAL_FILE_NM , B.FILE_EXTN , B.FILE_DSC , B.FILE_SIZE , A.REG_DTM FROM CO_FILE_MST A, CO_FILE_DTL B WHERE A.ATCH_FILE_ID = B.ATCH_FILE_ID AND A.USE_YN = 'Y'
AND (B.SAVE_FILE_NM LIKE '%' || #{searchWrd} || '%' OR B.REAL_FILE_NM LIKE '%' || #{searchWrd} || '%')
ORDER BY A.ATCH_FILE_ID, B.FILE_SEQ ) T1 ) TBL WHERE RNUM BETWEEN #{firstIndex} + 1 AND #{firstIndex} + #{recordCountPerPage}

mapper.update:
UPDATE /* FileManageDAO.updateFileCnt */ CO_FILE_DTL
   SET CNT = CNT + 1
 WHERE ATCH_FILE_ID = #{atchFileId}
   AND FILE_SEQ = #{fileSeq}
