mapper.select:
WITH TBL_SPPL_SRVC(ACCNT_NO, APP_NO, SVC_NM) AS ( SELECT A.ACCNT_NO , B.APP_NO , D.SVC_NM || '(' || C.ISU_CNT || '매)' AS SVC_NM FROM MEM_PROD_ACCNT_SVC A, PROD_OPT_SVC_MST B, PROD_OPT_SVC_DTL C, OPT_SVC D WHERE A.SEQ = B.SEQ AND A.SEQ = C.SEQ AND C.OPT_SVC_CD = D.SEQ AND B.USE_YN = 'Y' AND D.USE_YN = 'Y' AND START_DT <= (SELECT JOIN_DT FROM MEM_PROD_ACCNT WHERE ACCNT_NO = #{item.accntNo}) AND A.ACCNT_NO IN
#{item.accntNo}
) SELECT /* MLAJoinPtcDAO.selectSpplSrvcList */ ACCNT_NO , APP_NO , LISTAGG(SVC_NM, ', ') WITHIN GROUP(ORDER BY ACCNT_NO DESC, APP_NO) AS SVC_NM FROM TBL_SPPL_SRVC A GROUP BY ACCNT_NO, APP_NO
